import { createReducer, on, createFeatureSelector, createSelector } from '@ngrx/store';
import * as DataActions from './state/data.actions';
const InitialDataState = {
  products: [{
    inventory_item_id: '',
    item_no: ''
  }],
  packages: [{
    package_desc: '',
    package_id: -1
  }]
};
const getDataState = createFeatureSelector('data');
export const getData = createSelector(getDataState, state => state);
export const getMaterials = createSelector(getDataState, state => state.products);
export const getPackages = createSelector(getDataState, state => state.packages);
export const dataReducer = createReducer(InitialDataState, on(DataActions.loadMaterials, state => {
  return Object.assign(Object.assign({}, state), {
    products: [{
      inventory_item_id: '',
      item_no: ''
    }]
  });
}), on(DataActions.loadMaterialsSuccess, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    products: action.products
  });
}), on(DataActions.loadPackages, state => {
  return Object.assign(Object.assign({}, state), {
    packages: [{
      package_id: -1,
      package_desc: ''
    }]
  });
}), on(DataActions.loadPackagesSuccess, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    packages: action.packages
  });
}));
