import { createReducer, on, createFeatureSelector, createSelector } from '@ngrx/store';
import * as UserActions from './state/user.actions';
const InitialUserState = {
  employee_id: '',
  first_name: '',
  last_name: '',
  login: '',
  email: '',
  locid: -1,
  plant_restricted: false,
  super_user: false,
  dbVersion: '',
  serverName: '',
  emc_applications: [{
    emc_app_id: -1,
    emc_app_desc: '',
    emc_app_url: '',
    emc_access_level: -1
  }]
};
const getUserState = createFeatureSelector('data');
export const getUser = createSelector(getUserState, state => state);
export const userReducer = createReducer(InitialUserState, on(UserActions.loadUserInfo, state => {
  return Object.assign({}, state);
}), on(UserActions.setUserInfo, (state, action) => {
  return {
    employee_id: action.user.employee_id,
    first_name: action.user.first_name,
    last_name: action.user.last_name,
    email: action.user.email,
    login: action.user.login,
    locid: action.user.locid,
    dbVersion: action.user.dbVersion,
    plant_restricted: action.user.plant_restricted,
    super_user: action.user.super_user,
    emc_applications: action.user.emc_applications
  };
}), on(UserActions.loadUserInfoSuccess, (state, action) => {
  if (action.user && action.user != undefined) return Object.assign(Object.assign({}, state), {
    employee_id: action.user.employee_id,
    first_name: action.user.first_name,
    last_name: action.user.last_name,
    email: action.user.email,
    login: action.user.login,
    locid: action.user.locid,
    plant_restricted: action.user.plant_restricted,
    super_user: action.user.super_user,
    emc_applications: action.user.emc_applications
  });
}));
