const hostname = window.location.hostname;
let serverIndex = 0;
let app = 'MaterialRequestBE/api/'; // Rule 1: alphaapps (fixed)

if (hostname.includes('alphaapps.aoc')) {
  serverIndex = 0;
} // Rule 2: dynamic <number>x servers (181x, 281x, 381x…)
else if (/\d+x/.test(hostname)) {
  serverIndex = 1;
} // Rule 3: dynamic <number>sc servers (181sc, 281sc…)
else if (/\d+sc/.test(hostname)) {
  serverIndex = 3;
} // Rule 4: dynamic <number>s servers (181s, 281s…)
else if (/\d+s/.test(hostname)) {
  serverIndex = 4;
} // Rule 5: localhost
else if (hostname.includes('localhost')) {
  serverIndex = 1;
} // Rule 6: default (production fallback)
else {
  serverIndex = 1;
  app = 'api/';
}

export const AppConfig = {
  serverIndex,
  app
};
